mapper.select:
		
		
		WITH TMP AS (
	          SELECT 
	          /*+ INDEX_DESC(TBL_BOARD PK_BOARD) */
	          ROWNUM RN, TBL_BOARD.*
	          FROM TBL_BOARD
	          WHERE 
          
          	
	           
          
	          BNO > 0
	          AND ROWNUM <= #{pageNum} * #{amount}
	         )
         SELECT BNO, TITLE, CONTENT, WRITER, REGDATE,UPDATEDATE FROM TMP
         WHERE RN > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE TBL_BOARD SET
TITLE = #{title}
,CONTENT =
#{content}
,WRITER = #{writer}
,UPDATEDATE = SYSDATE
WHERE BNO = #{bno}
